import React from 'react';
import './App.css';



class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      allList: [],
    };
  }

  componentDidMount(){

  }

  add(e){
    if(window.event.keyCode === 13){
      this.setState({allList:[...this.state.allList,e.target.value]})
      e.target.value = '' 
    }
  }

  changeAll(){

  }

  changeActive(){

  }

  changeCompleted(){

  }

  render(){
    return (
      <div className="App">
        <header>
          <p>
            To DO List
          </p>
        </header>
        <div className="class">
          <input type="text" onKeyDown={this.add.bind(this)} placeholder="What needs to be done?" />
          <div className="test">
            {this.state.allList.map((todo) => (
              <li key={todo.id}>{todo}</li>
            ))}
          </div>
        </div>
        <footer>
            <div>{this.state.allList.length} items left</div>
            <div className="btns">
                <div onClick={this.changeAll.bind(this)}>All</div>
                <div onClick={this.changeActive.bind(this)}>Active</div>
                <div onClick={this.changeCompleted.bind(this)}>Completed</div>
            </div>
        </footer>
      </div>
    );
  }
}

export default App;
