import React from 'react';
import './App.css';



class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      list: [],
    };
  }

  componentDidMount(){

  }

  add(e){
    if(window.event.keyCode === 13){
      console.log(e.target.value)
      this.state.list.push(e.target.value)
      console.log(this.state.list)
    }
  }

  render(){
    return (
      <div className="App">
        <header>
          <p>
            To DO List
          </p>
        </header>
        <div className="class">
          <input type="text" onKeyDown={this.add.bind(this)} placeholder="What needs to be done?" />
          <div>
            {this.state.list.map((todo) => (
              <div todo={todo} key={todo.id} />
            ))}
          </div>
        </div>
      </div>
    );
  }
}

export default App;
