import React from 'react';
import './App.css';



class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      list: [],
    };
  }

  componentDidMount(){

  }

  add(e){
    if(window.event.keyCode === 13){
      this.setState({list:[...this.state.list,e.target.value]})
      e.target.value = '' 
    }
  }

  render(){
    return (
      <div className="App">
        <header>
          <p>
            To DO List
          </p>
        </header>
        <div className="class">
          <input type="text" onKeyDown={this.add.bind(this)} placeholder="What needs to be done?" />
          <div className="test">
            {this.state.list.map((todo) => (
              <li key={todo.id}>{todo}</li>
            ))}
          </div>
        </div>
        <footer>
            <div>{this.state.list.length} items left</div>
            <div class="btns">
                <div onClick="">All</div>
                <div onClick={this.changeActive.bind(this)}>Active</div>
                <div onClick={this.changeCompleted.bind(this)}>Completed</div>
            </div>
        </footer>
      </div>
    );
  }
}

export default App;
