import React from 'react';
import './App.css';



class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      list: [],
      defaultChecked: false
    };
  }

  componentDidMount(){

  }

  add(e){
    if(window.event.keyCode === 13){
      this.setState({list:[...this.state.list,e.target.value]})
      e.target.value = '' 
    }
  }

  changeAll(){

  }

  changeActive(){

  }

  changeCompleted(){

  }

  toggleChecked(e){
    console.log(e)
  }

  render(){
    return (
      <div className="App">
        <header>
          <p>
            To DO List
          </p>
        </header>
        <div className="class">
          <input className="input" type="text" onKeyDown={this.add.bind(this)} placeholder="What needs to be done?" />
          <div className="listBox">
            {this.state.list.map((todo) => (
              <li key={todo.id}>{todo}
                <input type="checkbox" onChange={this.toggleChecked.bind(this)} checked={this.state.defaultChecked} />
              </li>

            ))}
          </div>
        </div>
        <footer>
            <div className="left">{this.state.list.length} items left</div>
            <div className="btns right">
                <div onClick={this.changeAll.bind(this)}>All</div>
                <div onClick={this.changeActive.bind(this)}>Active</div>
                <div onClick={this.changeCompleted.bind(this)}>Completed</div>
            </div>
        </footer>
      </div>
    );
  }
}

export default App;
