import React from 'react';
import './App.css';



class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {data: []};
  }

  render(){
    return (
      <div className="App">
        <header>
          <p>
            To DO List
          </p>
        </header>
        <div>
          <input id="input" type="text" onChange="add()" placeholder="What needs to be done?" />
          <div>
            {this.state.data.map((todo) => (
              <div comment={todo} key={todo.id} />
            ))}
          </div>
        </div>
      </div>
    );
  }
}

export default App;
